tool_input.old_string:
execute(command: string): void {
  if (command === 'L') {
    this.turnLeft();
  } else if (command === 'R') {
    this.turnRight();
  }
}

tool_input.new_string:
execute(commands: string): void {
  for (const command of commands) {
    if (command === 'L') {
      this.turnLeft();
    } else if (command === 'R') {
      this.turnRight();
    }
  }
}
